INSERT INTO TF_B_TRADE_GRP_PLATSVC(TRADE_ID, ACCEPT_MONTH, USER_ID, SERV_CODE, SERIAL_NUMBER, GROUP_ID, BIZ_TYPE_CODE, BIZ_CODE, BIZ_ATTR, BIZ_NAME, BIZ_IN_CODE, BIZ_STATE_CODE, BIZ_STATUS, BIZ_PRI, OPER_STATE, AUTH_CODE, USAGE_DESC, INTRO_URL, BILLING_TYPE, BILLING_MODE, PRICE, PRE_CHARGE, CS_TEL, CS_URL, ACCESS_MODE, ACCESS_NUMBER, SI_BASE_IN_CODE, SI_BASE_IN_CODE_A, EC_BASE_IN_CODE, EC_BASE_IN_CODE_A, MAX_ITEM_PRE_DAY, MAX_ITEM_PRE_MON, DELIVER_NUM, FORBID_START_TIME_A, FORBID_END_TIME_A, FORBID_START_TIME_B, FORBID_END_TIME_B, FORBID_START_TIME_C, FORBID_END_TIME_C, FORBID_START_TIME_D, FORBID_END_TIME_D, IS_TEXT_ECGN, DEFAULT_ECGN_LANG, TEXT_ECGN_EN, TEXT_ECGN_ZH, FIRST_DATE, OPR_EFF_TIME, OPR_SEQ_ID, PLAT_SYNC_STATE, ADMIN_NUM, MAS_ID, START_DATE, END_DATE, MODIFY_TAG, UPDATE_DEPART_ID, UPDATE_STAFF_ID, UPDATE_TIME, REMARK, RSRV_NUM1, RSRV_NUM2, RSRV_NUM3, RSRV_NUM4, RSRV_NUM5, RSRV_STR1, RSRV_STR2, RSRV_STR3, RSRV_STR4, RSRV_STR5, RSRV_DATE1, RSRV_DATE2, RSRV_DATE3, RSRV_TAG1, RSRV_TAG2, RSRV_TAG3, SERVICE_ID) 
SELECT to_number(:TRADE_ID) TRADE_ID , TO_NUMBER(SUBSTR(:TRADE_ID,5,2)), to_number(:USER_ID) ,SERV_CODE,SERIAL_NUMBER, GROUP_ID, BIZ_TYPE_CODE, BIZ_CODE, BIZ_ATTR, BIZ_NAME, BIZ_IN_CODE,:BIZ_STATE_CODE,BIZ_STATUS,BIZ_PRI,:OPER_STATE, AUTH_CODE, USAGE_DESC, INTRO_URL, BILLING_TYPE, BILLING_MODE, PRICE, PRE_CHARGE, CS_TEL, CS_URL, ACCESS_MODE, ACCESS_NUMBER, SI_BASE_IN_CODE, SI_BASE_IN_CODE_A, EC_BASE_IN_CODE, EC_BASE_IN_CODE_A, to_number(MAX_ITEM_PRE_DAY) , to_number(MAX_ITEM_PRE_MON) , to_number(DELIVER_NUM) , FORBID_START_TIME_A, FORBID_END_TIME_A, FORBID_START_TIME_B, FORBID_END_TIME_B, FORBID_START_TIME_C, FORBID_END_TIME_C, FORBID_START_TIME_D, FORBID_END_TIME_D, IS_TEXT_ECGN, DEFAULT_ECGN_LANG, TEXT_ECGN_EN, TEXT_ECGN_ZH, FIRST_DATE , OPR_EFF_TIME, OPR_SEQ_ID, PLAT_SYNC_STATE, ADMIN_NUM, MAS_ID, START_DATE , END_DATE ,
 :MODIFY_TAG, :UPDATE_DEPART_ID, :UPDATE_STAFF_ID, to_date(:UPDATE_TIME,'yyyy-mm-dd hh24:mi:ss') , REMARK, RSRV_NUM1, RSRV_NUM2, RSRV_NUM3, to_number(RSRV_NUM4) , to_number(RSRV_NUM5) , RSRV_STR1, RSRV_STR2, RSRV_STR3, RSRV_STR4, RSRV_STR5, RSRV_DATE1 , RSRV_DATE2, RSRV_DATE3 , RSRV_TAG1, RSRV_TAG2, RSRV_TAG3, SERVICE_ID
FROM TF_F_USER_GRP_PLATSVC P
WHERE  
P.PARTITION_ID = MOD(TO_NUMBER(:USER_ID), 10000)
AND P.USER_ID=:USER_ID
AND sysdate between p.start_date and p.end_date
AND EXISTS (SELECT 1 FROM TF_F_CUST_GROUP G, TF_F_USER U
               WHERE U.USER_ID = P.USER_ID
               AND U.PARTITION_ID = MOD(TO_NUMBER(P.USER_ID), 10000)
               AND G.CUST_ID = U.CUST_ID
               AND G.REMOVE_TAG = '0')