insert into tf_b_trade_product(TRADE_ID,ACCEPT_MONTH,USER_ID,USER_ID_A,PRODUCT_ID,PRODUCT_MODE,BRAND_CODE,OLD_PRODUCT_ID,OLD_BRAND_CODE,INST_ID,CAMPN_ID,START_DATE,END_DATE,MODIFY_TAG,UPDATE_TIME,UPDATE_STAFF_ID,UPDATE_DEPART_ID,REMARK,RSRV_NUM1,RSRV_NUM2,RSRV_NUM3,RSRV_NUM4,RSRV_NUM5,RSRV_STR1,RSRV_STR2,RSRV_STR3,RSRV_STR4,RSRV_STR5,RSRV_DATE1,RSRV_DATE2,RSRV_DATE3,RSRV_TAG1,RSRV_TAG2,RSRV_TAG3)
select TO_NUMBER(:TRADE_ID),TO_NUMBER(SUBSTRB(:TRADE_ID,5,2)),USER_ID,USER_ID_A,PRODUCT_ID,PRODUCT_MODE,NVL(BRAND_CODE,'-1') BRAND_CODE,PRODUCT_ID,BRAND_CODE,substr('7405010576657759',0,2)||substr(f_sys_getseqid('ZZZZ','seq_inst_id'),3) inst_id,CAMPN_ID,SYSDATE,END_DATE,'0',UPDATE_TIME,UPDATE_STAFF_ID,UPDATE_DEPART_ID,REMARK,RSRV_NUM1,RSRV_NUM2,RSRV_NUM3,RSRV_NUM4,RSRV_NUM5,RSRV_STR1,RSRV_STR2,RSRV_STR3,RSRV_STR4,RSRV_STR5,RSRV_DATE1,RSRV_DATE2,RSRV_DATE3,RSRV_TAG1,RSRV_TAG2,RSRV_TAG3
from tf_b_trade_product_bak
WHERE trade_id = TO_NUMBER(:OLDTRADE_ID)
  AND accept_month = TO_NUMBER(SUBSTR(:OLDTRADE_ID,5,2))
 AND end_date > SYSDATE