SELECT user_id,acyc_id,max(acct_id) acct_id,max(partition_id) partition_id,integrate_item_code,sum(fee) fee,sum(balance) balance,sum(print_fee) print_fee,sum(a_discnt) a_discnt,sum(b_discnt) b_discnt,sum(adjust_before) adjust_before,sum(adjust_after) adjust_after FROM ts_a_bill_r WHERE acct_id=:ACCT_ID AND partition_id=:PARTITION_ID AND acyc_id=:ACYC_ID GROUP BY user_id,integrate_item_code,acyc_id